<script>
import global from "~/mixins/global";
export default {
  data(){
    return{
      formHandle: 'subscribe',
      request: 'subscribe',
      fields:{
        "email":{
          "value": '',
          "error": '',
          "type": 'email',
          "required": true
        },
      }
    }
  },
  props:{
    common: Object
  },
  mixins: [global]
}

</script>

<style lang='scss' scoped>
  .footer{
    background: var(--color-2);
    font-size: 1rem;
  }
  .footer__upper{
    display: grid;
    grid-template-columns: 1fr repeat(3, auto);
    justify-items: end;
    align-items: center;
    border-bottom: 1px solid #FFFFFF;
    padding: 4.6em 0 1.7em;
    grid-gap: 3em;
  }
  .footer-logo{
    display: block;
    max-width: 176px;
    justify-self: start;
  }
  
  .footer__link,
  .footer__link:visited{
    text-decoration: none;
    font-weight: normal;
    font-size: 1.8em;
    line-height: 122%;
    color: var(--color-7);
    display: grid;
    grid-template-columns: auto 1fr;
    grid-gap: 0 1em;
    align-items: center;
  }
  .footer__link-icon{
    display: block;
    width: 1.77em;
    height: 1.77em;
  }
  .footer__btnCall{
    color: var(--color-7);
    font-weight: normal;
    font-size: 1.8em;
    line-height: 122%;
    background: none;
    border: none;
    border-bottom: 1px solid var(--color-7);
    padding: 0;
    transition: .3s;
    &:hover,
    &:focus{
      border-bottom: 1px solid transparent;
    }
  }
  .footer-nav{
    grid-row: 1/4;
    grid-column: 1/2;
    align-self: start;
  }
